package txpool_test

import (
	"math/big"
	"testing"

	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/core/types"
	"github.com/lmittmann/w3"
	"github.com/lmittmann/w3/module/txpool"
	"github.com/lmittmann/w3/rpctest"
)

func TestContent(t *testing.T) {
	rpctest.RunTestCases(t, []rpctest.TestCase[*txpool.ContentResponse]{
		{
			Golden: "content",
			Call:   txpool.Content(),
			WantRet: &txpool.ContentResponse{
				Pending: map[common.Address][]*types.Transaction{
					w3.A("0x000454307bB96E303044046a6eB2736D2aD560B6"): {
						types.NewTx(&types.DynamicFeeTx{
							ChainID:   big.NewInt(1),
							Nonce:     4652,
							GasTipCap: big.NewInt(31407912032),
							GasFeeCap: big.NewInt(202871575924),
							Gas:       1100000,
							To:        w3.APtr("0xEf1c6E67703c7BD7107eed8303Fbe6EC2554BF6B"),
							Value:     big.NewInt(81000000000000000),
							Data:      w3.B(""),
							R:         new(big.Int).SetBytes(w3.B("0xf91729846ac8bb780a7239b7f0157d53330bba310a0811f4eec2eae25172c252")),
							S:         new(big.Int).SetBytes(w3.B("0x1bb9a1b9aea8b128e0e8dc42b17664be50c7b4073973c730b6c4cf2a3b3503cb")),
						}),
					},
				},
				Queued: map[common.Address][]*types.Transaction{
					w3.A("0x1BA4Ca9ac6ff4CF192C11E8C1624563f302cAA87"): {
						types.NewTx(&types.DynamicFeeTx{
							ChainID:   big.NewInt(1),
							Nonce:     183,
							GasTipCap: big.NewInt(110000000),
							GasFeeCap: big.NewInt(20027736270),
							Gas:       99226,
							To:        w3.APtr("0x1BA4Ca9ac6ff4CF192C11E8C1624563f302cAA87"),
							Value:     big.NewInt(0),
							Data:      []byte{},
							R:         new(big.Int).SetBytes(w3.B("0xea35c7c0643b79664b0bbb7f42d64edd371508ae4c33c1f817a80a2655465935")),
							S:         new(big.Int).SetBytes(w3.B("0x76d39f794e9e1ee359d66b7d3b19b90aaf2051b2159c68f3bb8c954558989da8")),
						}),
					},
				},
			},
		},
	})
}

func TestContentFrom(t *testing.T) {
	rpctest.RunTestCases(t, []rpctest.TestCase[*txpool.ContentFromResponse]{
		{
			Golden: "contentFrom",
			Call:   txpool.ContentFrom(w3.A("0x1BA4Ca9ac6ff4CF192C11E8C1624563f302cAA87")),
			WantRet: &txpool.ContentFromResponse{
				Queued: []*types.Transaction{
					types.NewTx(&types.DynamicFeeTx{
						ChainID:   big.NewInt(1),
						Nonce:     183,
						GasTipCap: big.NewInt(110000000),
						GasFeeCap: big.NewInt(20027736270),
						Gas:       99226,
						To:        w3.APtr("0x1BA4Ca9ac6ff4CF192C11E8C1624563f302cAA87"),
						Value:     big.NewInt(0),
						Data:      []byte{},
						R:         new(big.Int).SetBytes(w3.B("0xea35c7c0643b79664b0bbb7f42d64edd371508ae4c33c1f817a80a2655465935")),
						S:         new(big.Int).SetBytes(w3.B("0x76d39f794e9e1ee359d66b7d3b19b90aaf2051b2159c68f3bb8c954558989da8")),
					}),
				},
			},
		},
	})
}
